use codec::{Decode, Encode};
// use parity_scale_codec_derive::{Encode, Decode};
use frame_support::{RuntimeDebug};
use scale_info::TypeInfo;
use sp_finality_grandpa::{AuthorityId, AuthoritySignature};
use sp_runtime::traits::Header as HeaderT;
// use sp_std::prelude::*;
use sp_runtime::{
    generic,
    traits::{BlakeTwo256},
};
use hex_literal::hex;
// default MFV settings
pub type BlockNumber = u32;
pub type Hasher = BlakeTwo256;
pub type Header = generic::Header<BlockNumber, Hasher>;

#[derive(Encode, Decode, RuntimeDebug, TypeInfo, Clone, PartialEq, Eq)]
pub struct GrandpaJustification<Header: HeaderT> {
    pub round: u64,
    pub commit:
        finality_grandpa::Commit<Header::Hash, Header::Number, AuthoritySignature, AuthorityId>,
    pub votes_ancestries: Vec<Header>,
}


fn main() {
    let hex: Vec<u8> = hex!("").to_vec();
    let justification =  GrandpaJustification::<Header>::decode(&mut &*hex)
        .unwrap();
    let authorities_set_id: u64 = 1438;
    println!("{:?}", justification);
    // let mut signature_buffer = Vec::new();

    for signed in &justification.commit.precommits {

        println!("Sig: {:?}", &signed.signature);

        if sp_finality_grandpa::check_message_signature(
            &finality_grandpa::Message::Precommit(signed.precommit.clone()),
            &signed.id,
            &signed.signature,
            9u64,
            1438u64,
            // &mut signature_buffer,
        ) {
            println!("woohoo");
        } else {
            println!("Error::InvalidAuthoritySignature");
            // return Err(Error::InvalidAuthoritySignature)
        }
    }
}

